var readChars = function readChars() {
	this.getCharsPO = function (obj, requestType) {
		if (obj == null || obj.length == 0 || requestType == null || requestType.length == 0) {
			return null;
		}

		return validateChars(obj);

		function validateChars(obj){
			var result = new Array();
			var charsLen = obj.versions[0].characteristics.length;

			for(var i=0; i < charsLen; i++ ){
				var keyValue = new Array();
				var char = obj.versions[0].characteristics[i];
				if(char.id != "versionIdentifier"){
					var charId = char.id;
					var charValue = null;
					if(char.versions[0].valueType == "String" || char.versions[0].valueType == "Number" 
						|| char.versions[0].valueType == "Date" || char.versions[0].valueType == "CodeTable"){
						if(char.versions[0].value){
							charValue = char.versions[0].value;
						}
					}
					keyValue[0] = charId;
					keyValue[1] = charValue;
					result[result.length] = keyValue;
				}

			}

			if(requestType == "RT_REST"){
				var childChars = ["specificationSubtype","family","category"];
				for(var k=0; k < childChars.length; k++){
					var keyValue = new Array();
					var childId = childChars[k];
					var childValue = null;
					if(childId == "specificationSubtype"){
						childValue = obj.versions[0].specificationSubtype;
					}else if(childId == "family"){
						childValue = obj.versions[0].family;
					}else if(childId == "category"){
						childValue = obj.versions[0].category;
					}

					keyValue[0] = childId;
					keyValue[1] = childValue;
					result[result.length] = keyValue;
				}

			}
			return result;
		}
	};

	this.getCharsPOP = function (obj, requestType) {
		if (obj == null || obj.length == 0 || requestType == null || requestType.length == 0) {
			return null;
		}

		return validatePop(obj);

		function validatePop(obj){
		    var popCharResult = new Array();

			var popLen = obj.versions[0].productOfferingPrices.length;
			for(var j=0; j < popLen; j ++){
			    var popIdValues = new Array();
				var pop = obj.versions[0].productOfferingPrices[j].versions[0];
				var popId = pop.id;
				var charsLen = pop.characteristics.length;
				var charValues = new Array();
				for(var i=0; i < charsLen; i++ ){
					var keyValue = new Array();
					var char = pop.characteristics[i];
					if(char.id != "ecmPriceAmount" && char.id != "externalId" && char.id != "priceSubType" 
						&& char.id != "priceType" && char.id != "popType"){
						var charId = char.id;
						var charValue = null;
						if(char.versions[0].value){
							charValue = char.versions[0].value;
						}

						keyValue[0] = charId;
						keyValue[1] = charValue;
						charValues[charValues.length] = keyValue;
					}
				}

				if (pop.priceSubType != null && pop.priceSubType != ""){						
					var keyValue = new Array();
					keyValue[0] = "priceSubType";
					keyValue[1] = obj.versions[0].priceSubType;	
					charValues[charValues.length] = keyValue;
				}	

				popIdValues[popIdValues.length] = popId;
				popIdValues[popIdValues.length] = charValues;
				popCharResult[popCharResult.length] = popIdValues;
			}
			return popCharResult;
		}

	};
};
